;********************************; PRINT HELP TEXT FOR MEMORY;********************************;HELP       LDX #$00       JSR PRTHP       JSR CLRENT       JMP READY;********************************; PRINT MESSAGE TO SCREEN; REG X CONTAINS INDEX INTO; MESSAGE TABLE;********************************PRTHP  STX INDEX ;$C57EPRTHPX LDX INDEX ;$C581       LDA HELPTX,X       PHP        AND #$7F       JSR CHROUT       INC INDEX       PLP        BPL PRTHPX       RTS ;********************************;;********************************HELPTX .BYTE $0D       .TEXT 'AUTO'       .BYTE $0D       .TEXT 'CHANGE'       .BYTE $0D       .TEXT 'CPUT'       .BYTE $0D       .TEXT 'DELETE'       .BYTE $0D       .TEXT 'FIND'       .BYTE $0D       .TEXT 'FORMAT'       .BYTE $0D       .TEXT 'GET'       .BYTE $0D       .TEXT 'KILL'       .BYTE $0D       .TEXT 'NUMBER'       .BYTE $0D       .TEXT 'PUT'       .BYTE $0D       .TEXT 'CLS'       .BYTE $8D ; $0D WITH BIT 8 SET;********************************.END