;.PAGE 'PRG2BYTE';.OPT LIST;**********************************=$0801        ;START OF BASIC;*********************************; SET BSTUB TO ONE TO ALLOW FOR; INSERTION OF BASIC STUB CODE;*********************************BSTUB = 1;*********************************;;         PRG2BYTE64;      PRG2BYTE.C64 V072882;     CONVERT PROGRAM FILE;  TO ASSEMBLER64 SOURCE FILE;          BYTE,WORD ;; (C) 1982 BY COMMODORE MACHINES;;  WRITTEN BY DENTON MARLOWE;*********************************;; BASIC FORMAT PROGRAM;; 10 SYS(2063);;*********************************.IF  BSTUB == 1;.IFN BSTUB <;*********************************HH0801 .WORD BASIC    ;LINE LINK POINTER       .BYTE $0A,$00  ;LINE NUMBER (10)       .BYTE $9E      ;SYS COMMAND TOKEN       .TEXT '(2063)' ;CALL TO $080F       .BYTE $00      ;END OF LINEBASIC  .BYTE $00,$00  ;END OF BASIC;*********************************;>.FI;*********************************; C64 SPECIFIC EQUATES;*********************************BASICW = $E386 ;BASIC WARM STARTCHROUE = $E716 ;OUTPUT CHAR TO SCREENSCROLY = $D011 ;SCREEN CONTROL;*********************************;*=$080F        ;PROGRAM64 ORGIN;*********************************; JUMP OVER ; PROCESSOR SPECIFIC UTILITIES;*********************************       JMP START;*********************************; SET FLAG TO ALLOW BASIC; AND KERNAL (ERROR) MESSAGES; $80 FOR BASIC; $40 FOR KERNAL;*********************************;;*********************************SETMSG=$FF90;*********************************START  LDA #$40     ;ALLOW KERNAL MESSAGES       JSR SETMSG   ;KERNAL FLAG;*********************************; MAIN BODY OF CODE;*********************************.INCLUDE "prg2byte.asm";*********************************.END